import React, { Component } from 'react';
import { ListGroup, CardGroup, Card, Jumbotron, Container, Row, Col, Image, Button } from 'react-bootstrap';
import './SecondSection.css';

export default class SecondSection extends Component {
    state = {
      };

    render() {
        return (
            <Container>
                <Col sm={12} md={5}>
                    <figure>
                        <div id="secLeftDiv">
                            <img src="assets/Lighting.jpg" className="secondSection-image"/>
                        </div>
                        <figcaption id="capLeft">In the final installation, the portraits may rotate around their
                            suspension cables.
                            At night, the whole array will be illuminated by a color-changing LED lighting system.
                        </figcaption>
                    </figure>
                </Col>
                <Col sm={12} md={7}>
                    <figure>
                        <div class="flex">
                            <img id="hideSecond" src="assets/p2_img2.jpg" className="secondSection-image"/>
                            <img src="assets/p2_img3.jpg" className="secondSection-image"/>
                            <img src="assets/p2_img4.jpg" className="secondSection-image"/>
                        </div>
                        <figcaption id="capRight">The suspended sculpture will be composed of 300 dichroic prisms and
                            340 <span class="italicBold">portraits of
                                Iowa</span> printed on a acrylic sheets.
                        </figcaption>
                    </figure>
                </Col>
            </Container>
        );
    }
}
