import React, { Component } from 'react';
import { ListGroup, CardGroup, Card, Jumbotron, Container, Row, Col, Image, Button } from 'react-bootstrap';
import './SecondSection.css';

export default class SecondSection extends Component {
    state = {
      };

    render() {
        return (
            <Container>
                <Col sm={12} md={5}>
                    <Col>
                        <div id="secLeftDiv">
                            <Image src="assets/Lighting.jpg" className="secondSection-image"/>
                        </div>
                        <figcaption id="capLeft">In the final installation, the portraits may rotate around their
                            suspension cables.
                            At night, the whole array will be illuminated by a color-changing LED lighting system.
                        </figcaption>
                    </Col>
                </Col>
                <Col sm={12} md={7}>
                        <Col class="flex">
                            <Image id="hideSecond" src="assets/p2_img2.jpg" className="secondSection-image"/>
                            <Image src="assets/p2_img3.jpg" className="secondSection-image"/>
                            <Image src="assets/p2_img4.jpg" className="secondSection-image"/>
                        </Col>
                        {/* <figcaption id="capRight">
                            The suspended sculpture will be composed of 300 dichroic prisms and 340 <span class="italicBold">portraits of Iowa</span> printed on a acrylic sheets.
                        </figcaption> */}
                </Col>
            </Container>
        );
    }
}
