import React, { Component } from 'react';
import { ListGroup, CardGroup, Card, Jumbotron, Container, Row, Col, Image, Button } from 'react-bootstrap';
import './FirstSection.css';

export default class FirstSection extends Component {
    state = {
      };

    render() {
        return (
            <Container>
                <article id="top">
                    <h2>Photos of Cedar Rapids Wanted</h2>
                    <p> In September of 2020, artist John Fleming and the City of Cedar Rapids will install <span className="italicBold">Portraits of Iowa</span> inside the Cedar Rapids U.S. Cellular Center. We are looking for photos to be suspended within the sculpture, photos that capture the essence of Cedar Rapids and surrounding communities.</p>
                    <p>Please scroll down for more information about the sculpture and how to participate as a photographer.</p>
                </article>
                    <Col>
                        <h2>How to submit</h2>
                        <h4>You may submit up to 5 photos, here in this green box.</h4>
                        <p>Upload color jpgâ€™s, 2MB max per image. Please, no closeups of people. If we receive an over-abundance of images, we apologize in advance if one of yours is not selected.
                            <span className="deadLine">Submissions will be accepted until July 10th.</span>
                        </p>
                        
                        <div>

                        </div>
                    </Col>

                <Col className="right flex">
                    <div>
                        <img src="images/Fleming-1.jpg"/>
                        <p><span className="italic">Portraits of Iowa</span> at night</p>
                    </div>
                    <div>
                        <img src="images/Lantern-view-3.jpg"/>
                        <p>Photos suspended inside the Lantern</p>
                    </div>
                </Col>
            </Container>
        );
    }
}
