import React, { Component } from 'react';
// import { NavLink } from 'react-router-dom';
import { HashLink as Link } from 'react-router-hash-link';
import './CustomNavbar.css';
import firebase, { auth, provider } from '../../firebase.js';

export default class CustomNavbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      menu: false,
      username: '',
      user: null
    };
    this.toggleMenu = this.toggleMenu.bind(this);
  }

  // Collapse handled with React instead of bootstrap
  toggleMenu(){
    this.setState({ menu: !this.state.menu })
  }

  render() {
    const show = (this.state.menu) ? "show" : "" ;

    return (
    <nav className="navbar fixed-top navbar-expand-lg navbar-dark" style={{backgroundColor: "#303030e6", borderBottom: "1px solid #ff00ff"}}>
      <Link
        className="navbar-brand"
        smooth
        to="#first"
        // activeClassName="selected"
      >Portraits of Iowa</Link>
      {this.state.user ?
        <button onClick={this.logout}>Log Out</button>                
        :
        <button onClick={this.login}>Log In</button>              
      }
      <button className="navbar-toggler" type="button" onClick={ this.toggleMenu }> 
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className={"collapse navbar-collapse " + show}>
        <div className="navbar-nav ml-auto">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item" >
              <Link
                smooth
                to="#second"
                // activeClassName="selected"
              >about the sculpture</Link>
            </li>
            <li className="nav-item" >
              <Link
                smooth
                to="#third"
                // activeClassName="selected"
              >about the photos</Link>
            </li>
            <li className="nav-item" >
              <Link
                smooth
                to="#fourth"
                // activeClassName="selected"
              >more info</Link>
            </li>
          </ul>
          {/* <ul className="navbar-nav mr-auto">
            <li className="nav-item" >
              <NavLink onClick={ this.toggleMenu } className="nav-link" exact to="/" activeClassName="active">Home <span className="sr-only">(current)</span></NavLink>
            </li>
            <li className="nav-item">
              <NavLink onClick={ this.toggleMenu } className="nav-link" to="/about" activeClassName="active">About</NavLink>
            </li>
            <li className="nav-item">
              <NavLink onClick={ this.toggleMenu } className="nav-link" to="/contact" activeClassName="active">Contact</NavLink>
            </li>
          </ul> */}
        </div>
      </div>
    </nav>
    );
  }
}

